import unittest
from datetime import datetime, timezone

# Assume date_parsing_function is defined elsewhere and imported
# For testing, a stub is provided below.  Replace with your actual function.
def date_parsing_function(date_string):
    """Stub for the date parsing function.  Replace with your actual implementation."""
    try:
        return datetime.fromisoformat(date_string)
    except ValueError:
        return None

class DateParsingTests(unittest.TestCase):

    def test_valid_date_isoformat(self):
        self.assertEqual(date_parsing_function("2023-10-26T10:00:00"), datetime(2023, 10, 26, 10, 0, 0))
        self.assertEqual(date_parsing_function("2024-01-01T00:00:00Z"), datetime(2024, 1, 1, 0, 0, 0, tzinfo=timezone.utc))
        self.assertEqual(date_parsing_function("2023-12-31T23:59:59"), datetime(2023, 12, 31, 23, 59, 59))

    def test_valid_date_no_time(self):
        self.assertEqual(date_parsing_function("2023-10-26"), datetime(2023, 10, 26, 0, 0, 0))
        self.assertEqual(date_parsing_function("2024-01-01"), datetime(2024, 1, 1, 0, 0, 0))

    def test_invalid_date_format(self):
        self.assertIsNone(date_parsing_function("10/26/2023"))
        self.assertIsNone(date_parsing_function("26-10-2023"))
        self.assertIsNone(date_parsing_function("October 26, 2023"))

    def test_leap_year(self):
        self.assertEqual(date_parsing_function("2024-02-29"), datetime(2024, 2, 29, 0, 0, 0))
        self.assertEqual(date_parsing_function("2024-02-29T12:00:00"), datetime(2024, 2, 29, 12, 0, 0))

    def test_non_leap_year_feb_29(self):
        self.assertIsNone(date_parsing_function("2023-02-29"))
        self.assertIsNone(date_parsing_function("2023-02-29T12:00:00"))

    def test_dst_transition_spring_forward(self):
        # US Eastern Time - DST starts March 10, 2024 at 2:00 AM
        self.assertEqual(date_parsing_function("2024-03-10T01:59:59"), datetime(2024, 3, 10, 1, 59, 59))
        self.assertEqual(date_parsing_function("2024-03-10T02:00:00"), datetime(2024, 3, 10, 3, 0, 0)) #Skips to 3 due to DST
        self.assertEqual(date_parsing_function("2024-03-09T23:59:59"), datetime(2024, 3, 9, 23, 59, 59))

    def test_dst_transition_fall_back(self):
        # US Eastern Time - DST ends Nov 3, 2024 at 2:00 AM
        self.assertEqual(date_parsing_function("2024-11-03T01:59:59"), datetime(2024, 11, 3, 1, 59, 59))
        self.assertEqual(date_parsing_function("2024-11-03T02:00:00"), datetime(2024, 11, 3, 1, 0, 0)) #Goes back to 1 due to DST
        self.assertEqual(date_parsing_function("2024-11-02T23:59:59"), datetime(2024, 11, 2, 23, 59, 59))

    def test_invalid_date_values(self):
        self.assertIsNone(date_parsing_function("2023-02-30"))
        self.assertIsNone(date_parsing_function("2023-04-31"))
        self.assertIsNone(date_parsing_function("2023-13-01"))
        self.assertIsNone(date_parsing_function("2023-10-32"))

    def test_empty_string(self):
        self.assertIsNone(date_parsing_function(""))

    def test_timezone_aware(self):
        self.assertEqual(date_parsing_function("2023-10-26T10:00:00+02:00"), datetime(2023, 10, 26, 10, 0, 0, tzinfo=timezone(3600, 7200)))
        self.assertEqual(date_parsing_function("2023-10-26T10:00:00-05:00"), datetime(2023, 10, 26, 10, 0, 0, tzinfo=timezone(-18000, -18000)))
        self.assertEqual(date_parsing_function("2024-01-01T00:00:00Z"), datetime(2024, 1, 1, 0, 0, 0, tzinfo=timezone.utc))



if __name__ == '__main__':
    unittest.main()
