def remove_duplicate_customers(customer_list):
    """
    Removes duplicate customers from a list, keeping the most complete record.

    Args:
        customer_list: A list of dictionaries, where each dictionary represents a customer.

    Returns:
        A list of dictionaries with duplicate customers removed, keeping the most complete record.
    """
    unique_customers = {}
    for customer in customer_list:
        email = customer.get('email')
        if email:
            if email not in unique_customers:
                unique_customers[email] = customer
            else:
                # Keep the customer with more keys
                if len(customer) > len(unique_customers[email]):
                    unique_customers[email] = customer
    return list(unique_customers.values())

